{!REQUIRESCRIPT("/soap/ajax/10.0/apex.js")}

var id = "{!Account.Id}"; 

var conf = sforce.connection.query("Select CRM_Safra_Actual__c From Configuracion_BMX__c limit 1");
records = conf.getArray("records"); 
var safra = records[0].CRM_Safra_Actual__c.toString();

var qry = "Select Id From CRM_Multiplicador__c Where Cuenta__c = '" + id + "' And Safra__c = '" + safra + "' limit 1";

var crm = sforce.connection.query(qry);

records2 = crm.getArray("records");

if (records2 == ''){
    alert('No existe CRM');
}else{
    window.open('/apex/CRM_Infraestructura?id=' + "{!Account.Id}" + '&nombre=' + "{!Account.Name}", "_self");
}
