WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/10.0/connection.js")} 
{!REQUIRESCRIPT("/soap/ajax/10.0/apex.js")} 

var rt = sforce.connection.query("SELECT Id FROM RecordType WHERE Name = 'RECLAMO VISITA CAMPO CONF' limit 1");
records = rt.getArray("records"); 
rt_caso = records[0].Id.toString(); 

var caseObj = new sforce.SObject("Case"); 
caseObj.Id = '{!Case.Id}'; 
caseObj.RecordTypeId = rt_caso; //Poner id del recordtype en cuestion 
caseObj.Status = 'Reclamo Visita Campo Confirmada';
caseObj.Visita_Campo_Rechazada__c = 'false';
var result = sforce.connection.update([caseObj]); 
if (result[0].success=='false') { 
alert(result[0].errors.message); 
} else { 
window.location.href=window.location.href; 
}
