WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/10.0/connection.js")} 
{!REQUIRESCRIPT("/soap/ajax/10.0/apex.js")} 

var rt;
var atendimento = "{!Case.Reclamo_con_atendimento__c}";
var status;

if (atendimento == 1){
    rt = sforce.connection.query("SELECT Id FROM RecordType WHERE Name = 'RECLAMO EN ANDAMENTO' limit 1");       
    status = 'Reclamo en Andamento';  
}else{
    rt = sforce.connection.query("SELECT Id FROM RecordType WHERE Name = 'RECLAMO NUEVO' limit 1");
    status = 'Aberta';
} 

records = rt.getArray("records");
rt_caso = records[0].Id.toString();


var caseObj = new sforce.SObject("Case"); 
caseObj.Id = '{!Case.Id}'; 
caseObj.RecordTypeId = rt_caso; 
caseObj.Status = status; 
var result = sforce.connection.update([caseObj]); 
if (result[0].success=='false') { 
alert(result[0].errors.message); 
} else { 
window.location.href=window.location.href; 
}
