var id = "{!Opportunity.Id}"; 
var accId = "{!Opportunity.AccountId}"; 
var pasa = true; 
var etapa = "{!Opportunity.StageName}"; 
var tieneOpli = "{!Opportunity.HasOpportunityLineItem}"; 


if (etapa == 'AN - Anulada'){ 
alert("Fase para gerar a autorização não deve ser AN - Anulada."); 
pasa = false; 
}
if(tieneOpli == false){ 
alert("Opp sem Produtos."); 
pasa = false; 
} 
if(pasa == true){ 
var result = sforce.apex.execute("previsionesController","generarVenta", {opId : id, acId : accId, rechazo : true }); 
//window.location.href = result; 
alert(result[0]); 
if (result[1] == 'ok'){ 
var mywin = window.location.href='/' + result[2]; 
} 
}
