var StageName = "{!Opportunity.StageName}"; 
var NewStage = '';

if (StageName == 'PE - Pendiente') {
   NewStage = 'RR';
   }else if (StageName == 'AU - Autorizada') {
      NewStage = 'RE';
}

var result = sforce.apex.execute("Opp_SAP_Mod","statusSAP", {oppId : id, etapa : NewStage, target : 200, rechazar_y_liberar : false, dev : false}); 
alert(result[0]);
window.location.reload(true);

