fieldUpdates.formula:
IF(ISPICKVAL(Tipo_de_Cultivares__c, 'Lançamentos'),
			IF(Kg_de_produto__c >= Campaign.Kg_para_quarto_preco__c, Campaign.Quarto_Preco_Difusao__c,
    IF(Kg_de_produto__c >= Campaign.Kg_para_terceiro_prec__c, Campaign.Terceiro_Preco_Difusao__c,
        IF(Kg_de_produto__c >= Campaign.Kg_para_segundo_preco__c, Campaign.Segundo_Preco_Difusao__c, Campaign.Primeiro_Preco_Difusao__c
          )
    )
),
			0)

IF(Kg_de_produto__c >= Campaign.Kg_para_quarto_preco__c, Campaign.Quarto_Preco_Multiplicacao__c,
        IF(Kg_de_produto__c >= Campaign.Kg_para_terceiro_prec__c, Campaign.Terceiro_Preco_Multiplicacao__c,
            IF(Kg_de_produto__c >= Campaign.Kg_para_segundo_preco__c, Campaign.Segundo_Preco_Multiplicacao__c, 
			            Campaign.Primeiro_Preco_Multiplicacao__c
            )
				    )
				)
